void game_cycle(void)
{
    unsigned char k;
    boolean ve,pa;
    while(1){
        current_position=next_position;
        if(marker[120]==false&&(marker[121]||marker[122])) {
            clear();
            evidence1();
            cr=&world[search_room(current_position)];
            show_messagenlf(cr->short_d);
            printspace();
            end_evidence1();
            normaltxt();
            printspace();
            show_message(cr->long_d);
            printnewline();
            marker[120]=true;
            ve=false;
            for(k=0;k<OSIZE;++k)
                if(obj[k].position==current_position) {
                    if(ve==false) {
                        show_message(message1031);
                        ve=true;
                        evidence2();
                    }
                    show_message(obj[k].desc);
                }
            normaltxt();
            if(marker[124]) {
                pa=false;
                for(k=0; k<NDIR; ++k)
                    if(cr->directions[k]) {
                        if(pa==false) {
                            show_messagenlf(message1020);
                            pa=true;
                        }
                    evidence3();
                    show_messagenlf(dir[k]);
                    printspace();
                }
                normaltxt();
                printnewline();
            }
        }
        ++counter[125];
        --counter[126];
        --counter[127];
        --counter[128];
        hi_cond();
        if(retv) continue;
        printnewline();
        if(ls==0 && counter[125]<5) show_message(message1012);
        interrogationAndAnalysis();
        local_cond();
        if(retv) continue;
        low_cond();
        if(retv) continue;
        show_message(cv(0)?message1009:message1010);
    }
}

int main(void)
{
    restart();
    init_term();
    evidence2();
    show_message(header);
    normaltxt();
    waitkey();
    game_cycle();
    return 0;
}
